import React,{useRef, useState} from 'react'
import {
     CalendarIcon,
     EmojiHappyIcon,
     LocationMarkerIcon,
     PhotographIcon,
     SearchCircleIcon
     } from '@heroicons/react/outline'
import { useSession } from 'next-auth/react'
import { Tweet, TweetBody } from '../typings'
import { fetchTweets } from '../utils/fetchTweets'
import toast from 'react-hot-toast'



interface Props {
  setTweets:React.Dispatch<React.SetStateAction<Tweet[]>>
}

export default function Tweetbox({setTweets}:Props) {

    const [input,setInput]=useState<string>('')
    const [image,setImage]=useState<string>('')
    const {data:session}=useSession()

    const [isImgUrlBoxOpen,setIsImgUrlBoxOpen]=useState<boolean>(false)

    const imgInputRef=useRef<HTMLInputElement>(null)

    const addImage=(e:React.MouseEvent<HTMLButtonElement, globalThis.MouseEvent>)=>{
    e.preventDefault();
    if(!imgInputRef.current?.value) return
      setImage(imgInputRef.current.value)
      imgInputRef.current.value=''
      setIsImgUrlBoxOpen(false)

    }

    const postTweet= async()=>{

     const tweetInfo:TweetBody={
       text:input,
       username:session?.user?.name || "Unknown Username",
       profileImg:session?.user?.image|| "https://links.papareact.com/drq",
       image:image,

     }

     const result=await fetch(`${process.env.NEXT_PUBLIC_BASE_URL}api/addTweet`,{
       body:JSON.stringify(tweetInfo),  
        method:'POST',
     })
     const json=await result.json()

     const newTweets= await fetchTweets()
     
     setTweets(newTweets);

     toast("Tweet Posted")
    console.log(json)
     return json;
    }


    const handleSubmit=(e: React.MouseEvent<HTMLButtonElement, globalThis.MouseEvent>)=>{
     e.preventDefault()
     postTweet();
     setImage("")
      setInput("")
      setIsImgUrlBoxOpen(false)
    }


  return (
    <>
    <div className='flex space-x-2 p-5'>
           <img className="m-3 mt-4 h-14 w-14 rounded-full object-cover" src={session?.user?.image||"data:image/png;base64,"} alt=""/>
    <div className='flex flex-1 items-center pl-2'>
        <div className='flex fex-1 flex-col'>
            <input type="text"
            value={input}
            onChange={(e)=>setInput(e.target.value)}
            className='h-24 w-full outline-none text-xl placeholder:text-xl' placeholder="What's happening"/>
            <div className='flex items-center '>
                <div className='flex space-x-2 text-twitter flex-1'>
                    <PhotographIcon onClick={()=>setIsImgUrlBoxOpen(!isImgUrlBoxOpen)} className='h-5 w-5 cursor-pointer transition-transform duration-150 ease-out hover:scale-150'/>  
                 <SearchCircleIcon className='h-5 w-5'/>
                 <EmojiHappyIcon className='h-5 w-5'/>
                 <CalendarIcon className='h-5 w-5'/>
                 <LocationMarkerIcon className='h-5 w-5'/>
                </div>
               <button 
               onClick={handleSubmit}
               disabled={!input || !session}
               className='bg-twitter px-5 py-2 rounded-full font-bold text-white disabled:opacity-30'>
                 Tweet</button>
            </div>
            {
                isImgUrlBoxOpen && (
                  <form className='flex  mt-5 rounded-lg bg-twitter/80 py-2 px-4'>
                    <input 
                    ref={imgInputRef}
                    type="text"
                     placeholder='Enter Image Url.... ' className='outline-none flex-1 bg-transparent text-white
                     placeholder:text-white p-3'/>
                     
                    <button type='submit' onClick={addImage} className='font-bold text-white'>Add Image</button>
                    </form>
                )
            }
            {image && (<img src={image} className="m-5 ml-0 mb-1 mt-10 h-auto max-h-60 w-full rounded-lg object-contain shadow-sm"/>)}
        </div>
    </div>
    </div>
    </>
  )
}
